tinymce.PluginManager.add('insert_control_material', function(editor, url) {
    // Add a button that opens a window
    editor.addButton('insert_control_material', {
        tooltip: 'Добавить ссылку на контрольный материал',
        text: 'Тест',
        icon: false,
        onclick: function() {
            // Open window
            editor.windowManager.open({
                title: 'Вставить ссылку на контрольный материал',
                url: url+'/dialog.html',
                width: 400,
                height: 300,
            });
        }
    });
});